import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { FaUser, FaSignOutAlt, FaHome, FaBell, FaFileAlt, FaSubscript, FaCreditCard } from 'react-icons/fa';
import axios from 'axios';

const UserDashboard = () => {
  const { user, logout } = useAuth();
  const [userSubscriptions, setUserSubscriptions] = useState([]);
  const [userTransactions, setUserTransactions] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (user?.email) {
      fetchUserData();
    }
  }, [user]);

  const fetchUserData = async () => {
    try {
      const [subsRes, transRes] = await Promise.all([
        axios.get('http://localhost:8000/api/subscriptions/'),
        axios.get('http://localhost:8000/api/transactions/')
      ]);

      // Filter data for current user - only active subscriptions
      const userSubs = subsRes.data.filter(sub => 
        sub.user_email === user.email && sub.status === 'active'
      );
      const userTrans = transRes.data.filter(trans => trans.user_name && trans.user_name.includes(user.name));

      setUserSubscriptions(userSubs);
      setUserTransactions(userTrans);
    } catch (error) {
      console.error('Error fetching user data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    logout();
    window.location.href = '/login';
  };

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Header */}
      <header className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center">
              <FaHome className="text-green-600 text-2xl mr-3" />
              <h1 className="text-xl font-semibold text-gray-900">User Dashboard</h1>
            </div>
            <div className="flex items-center space-x-4">
              <span className="text-sm text-gray-700">Welcome, {user?.name || user?.email}</span>
              <button
                onClick={handleLogout}
                className="flex items-center text-gray-500 hover:text-gray-700 transition"
              >
                <FaSignOutAlt className="mr-1" />
                Logout
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          {/* Welcome Card */}
          <div className="bg-white shadow rounded-lg mb-6">
            <div className="px-4 py-5 sm:p-6">
              <h3 className="text-lg leading-6 font-medium text-gray-900 mb-2">
                Welcome back, {user?.name || 'User'}!
              </h3>
              <p className="text-sm text-gray-600">
                You have {userSubscriptions.length} active subscription(s) and {userTransactions.length} transaction(s).
              </p>
            </div>
          </div>

          {/* Stats Cards */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-6">
            <div className="bg-white overflow-hidden shadow rounded-lg">
              <div className="p-5">
                <div className="flex items-center">
                  <FaSubscript className="h-6 w-6 text-blue-600 mr-3" />
                  <div>
                    <p className="text-sm font-medium text-gray-500">My Subscriptions</p>
                    <p className="text-2xl font-bold text-gray-900">{userSubscriptions.length}</p>
                  </div>
                </div>
              </div>
            </div>
            <div className="bg-white overflow-hidden shadow rounded-lg">
              <div className="p-5">
                <div className="flex items-center">
                  <FaCreditCard className="h-6 w-6 text-green-600 mr-3" />
                  <div>
                    <p className="text-sm font-medium text-gray-500">Transactions</p>
                    <p className="text-2xl font-bold text-gray-900">{userTransactions.length}</p>
                  </div>
                </div>
              </div>
            </div>
            <div className="bg-white overflow-hidden shadow rounded-lg">
              <div className="p-5">
                <div className="flex items-center">
                  <FaBell className="h-6 w-6 text-yellow-600 mr-3" />
                  <div>
                    <p className="text-sm font-medium text-gray-500">Notifications</p>
                    <p className="text-2xl font-bold text-gray-900">3</p>
                  </div>
                </div>
              </div>
            </div>
            <div className="bg-white overflow-hidden shadow rounded-lg">
              <div className="p-5">
                <div className="flex items-center">
                  <FaUser className="h-6 w-6 text-purple-600 mr-3" />
                  <div>
                    <p className="text-sm font-medium text-gray-500">Account Status</p>
                    <p className="text-lg font-bold text-green-600">Active</p>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* My Subscriptions */}
          <div className="bg-white shadow rounded-lg mb-6">
            <div className="px-6 py-4 border-b border-gray-200">
              <h3 className="text-lg font-medium text-gray-900">My Subscriptions</h3>
            </div>
            <div className="p-6">
              {loading ? (
                <div className="animate-pulse space-y-4">
                  <div className="h-4 bg-gray-200 rounded w-3/4"></div>
                  <div className="h-4 bg-gray-200 rounded w-1/2"></div>
                </div>
              ) : userSubscriptions.length > 0 ? (
                <div className="space-y-4">
                  {userSubscriptions.map((subscription) => (
                    <div key={subscription.subscription_id} className="border border-gray-200 rounded-lg p-4">
                      <div className="flex justify-between items-start">
                        <div>
                          <h4 className="text-lg font-medium text-gray-900">{subscription.plan_name}</h4>
                          <p className="text-sm text-gray-600">{subscription.subscription_type} subscription</p>
                          <p className="text-sm text-gray-500">Started: {subscription.start_date}</p>
                        </div>
                        <div className="text-right">
                          <p className="text-lg font-bold text-gray-900">${subscription.plan_price}</p>
                          <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${
                            subscription.status === 'active' 
                              ? 'bg-green-100 text-green-800' 
                              : 'bg-red-100 text-red-800'
                          }`}>
                            {subscription.status}
                          </span>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <p className="text-gray-500 text-center py-8">No active subscriptions found.</p>
              )}
            </div>
          </div>

          {/* Recent Transactions */}
          <div className="bg-white shadow rounded-lg">
            <div className="px-6 py-4 border-b border-gray-200">
              <h3 className="text-lg font-medium text-gray-900">Recent Transactions</h3>
            </div>
            <div className="p-6">
              {loading ? (
                <div className="animate-pulse space-y-4">
                  <div className="h-4 bg-gray-200 rounded w-3/4"></div>
                  <div className="h-4 bg-gray-200 rounded w-1/2"></div>
                </div>
              ) : userTransactions.length > 0 ? (
                <div className="space-y-4">
                  {userTransactions.slice(0, 5).map((transaction) => (
                    <div key={transaction.billing_id} className="flex justify-between items-center border-b border-gray-100 pb-3">
                      <div>
                        <p className="text-sm font-medium text-gray-900">{transaction.plan_name}</p>
                        <p className="text-xs text-gray-500">{transaction.billing_date}</p>
                      </div>
                      <div className="text-right">
                        <p className="text-sm font-bold text-gray-900">${transaction.amount}</p>
                        <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${
                          transaction.payment_status === 'paid' 
                            ? 'bg-green-100 text-green-800' 
                            : 'bg-yellow-100 text-yellow-800'
                        }`}>
                          {transaction.payment_status}
                        </span>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <p className="text-gray-500 text-center py-8">No transactions found.</p>
              )}
            </div>
          </div>
        </div>
      </main>
    </div>
  );
};

export default UserDashboard;
