import React from 'react';

export const SimplePieChart = ({ data, title }) => {
  const total = data.active + data.inactive;
  const activePercentage = total > 0 ? (data.active / total) * 100 : 0;
  const inactivePercentage = total > 0 ? (data.inactive / total) * 100 : 0;

  return (
    <div className="text-center">
      <h3 className="text-lg font-medium text-gray-900 mb-4">{title}</h3>
      <div className="relative w-48 h-48 mx-auto">
        <svg viewBox="0 0 42 42" className="w-full h-full">
          <circle
            cx="21"
            cy="21"
            r="15.915"
            fill="transparent"
            stroke="#EF4444"
            strokeWidth="3"
            strokeDasharray={`${inactivePercentage} ${100 - inactivePercentage}`}
            strokeDashoffset="25"
          />
          <circle
            cx="21"
            cy="21"
            r="15.915"
            fill="transparent"
            stroke="#10B981"
            strokeWidth="3"
            strokeDasharray={`${activePercentage} ${100 - activePercentage}`}
            strokeDashoffset={`${25 - inactivePercentage}`}
          />
        </svg>
        <div className="absolute inset-0 flex items-center justify-center">
          <div className="text-center">
            <div className="text-2xl font-bold text-gray-900">{total}</div>
            <div className="text-sm text-gray-500">Total</div>
          </div>
        </div>
      </div>
      <div className="mt-4 flex justify-center space-x-6">
        <div className="flex items-center">
          <div className="w-3 h-3 bg-green-500 rounded-full mr-2"></div>
          <span className="text-sm text-gray-600">Active ({data.active})</span>
        </div>
        <div className="flex items-center">
          <div className="w-3 h-3 bg-red-500 rounded-full mr-2"></div>
          <span className="text-sm text-gray-600">Inactive ({data.inactive})</span>
        </div>
      </div>
    </div>
  );
};

export const SimpleBarChart = ({ data, title }) => {
  const maxValue = Math.max(...data.values);

  return (
    <div>
      <h3 className="text-lg font-medium text-gray-900 mb-4">{title}</h3>
      <div className="space-y-3">
        {data.labels.map((label, index) => {
          const value = data.values[index];
          const percentage = maxValue > 0 ? (value / maxValue) * 100 : 0;

          return (
            <div key={label} className="flex items-center">
              <div className="w-24 text-sm text-gray-600 text-right mr-3">
                {label}
              </div>
              <div className="flex-1 bg-gray-200 rounded-full h-6 relative">
                <div
                  className="bg-blue-500 h-6 rounded-full flex items-center justify-end pr-2"
                  style={{ width: `${percentage}%` }}
                >
                  <span className="text-white text-xs font-medium">{value}</span>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};
